layout.action:
logger.d("VideoListPage: page is ready!")
var groupSlider = this.findItemById("area_group_slider");
var firstGroupItem = groupSlider.children[0];
firstGroupItem.requestFocus();
firstGroupItem.fireEvent("click");
logger.d("VideoListPage: Selected group item is ", firstGroupItem.text)

select.action:
// 刷新影片列表后，回滚到初始位置
this.scrollTo(0,false);
// 清除旧的优先级
this.setDynamicFocus(null);

// group_label 被选中
logger.d("VideoListPage: group_label is checked");

// 刷新影片列表
var newDataUrl = this.getData("userdata");
logger.d("VideoListPage: new data url is ", newDataUrl);
this.ownerPage.refreshBinding("video_list_data", newDataUrl);

var parent = this.parent;
if ( parent.hasClass("item-container-expanded") ) {
parent.removeClass("item-container-expanded");
this.removeClass("content-label-expanded");
} else {
parent.addClass("item-container-expanded");
this.addClass("content-label-expanded");
}

// 滚动结束 处理输入框
var editBoxLabel = this.findItemById("edit_box_label");
if ((editBoxLabel != null) && (this.scrollPos == 0)) {
    // 已经滚动到editBox
    if (!editBoxLabel.hasClass("hide_item")) {
        // 需要显示edit box
        editBoxLabel.fireEvent("click");
    }
}

this.fireEvent("click");

var HIDE_ITEM_CLASS = "hide_indicator_image";
var slider = this.parent;
var children = slider.children;
logger.d("Demo: children count=", children.length);

// 获取对应的indicator image parent
var layerArea = slider.parent.parent;
var indicatorParent = layerArea.findItemById("indicator_area");

// 首项不显示上箭头，末项不显示下箭头，其他项上下箭头均显示
var indicatorUp = indicatorParent.findItemById("indicator_image_up");
var indicatorDown = indicatorParent.findItemById("indicator_image_down");
// children[0] 和 children[-1] 为placeholder，children[1] edit_box_label 需要排除
var firstItem;
var editBoxItem = slider.findItemsByClass("edit_box");
logger.d("Demo: editBoxItem count=", editBoxItem.length);
if ((editBoxItem.length > 0) && (this == children[1] || this == children[2])) {
    logger.d("Demo: indicatorUp hides", " slider has editbox item.");
    indicatorUp.addClass(HIDE_ITEM_CLASS);
} else if (this == children[1]) {
    logger.d("Demo: indicatorUp hides");
    indicatorUp.addClass(HIDE_ITEM_CLASS);
} else {
    indicatorUp.removeClass(HIDE_ITEM_CLASS);
}

// 当前项目为第一个时，仍然需要判断是否为最后一项
if (this == children[children.length-2]) {
    logger.d("Demo: indicatorDown hides");
    indicatorDown.addClass(HIDE_ITEM_CLASS);
} else {
    indicatorDown.removeClass(HIDE_ITEM_CLASS);
}

var HIDE_ITEM_CLASS = "hide_item";
var slider = this.parent;
logger.d("Demo: item lose focus");

var focusItem = this.ownerPage.currentFocus;
if (!focusItem.isChildOf(slider)) {
    // slider 失去焦点，需要隐藏slider，显示display label
    logger.d("Demo: currentFocus id=", focusItem.id);

    slider.addClass(HIDE_ITEM_CLASS);

    var layerArea = slider.parent.parent;
    var indicator = layerArea.findItemById("indicator_area");
    indicator.addClass(HIDE_ITEM_CLASS);

    var displayLabel = layerArea.findItemById("display_label");
    displayLabel.removeClass(HIDE_ITEM_CLASS);
    displayLabel.setAttr("text", this.getAttr("text"));
}

var editBoxLabel = this.parent.findItemById("edit_box_label");

var text = this.getAttr("text");
if (text == "") {
    text = this.getAttr("hint-text");
}
logger.d("Demo: custom_edit_box unchecked text=", text);
editBoxLabel.setAttr("text", text);
editBoxLabel.removeClass("hide_item");
this.addClass("hide_item");

var editBox = this.parent.findItemById("custom_edit_box");
var slider = editBox.parent;
if (editBox.parent.scrollDelta != 0) {
    // 滚动尚未结束，等待滚动结束时再继续
    return;
}

logger.d("Demo: edit_box_label checked.");
this.addClass("hide_item");

// 使用pushStates可使requestFocus即刻生效
this.ownerPage.pushStates(editBox, "removeClass", "hide_item",
    editBox, "requestFocus");
this.ownerPage.popStates(true);
logger.d("Demo: editBox get focus.");

var slider = this.parent;
var layerArea = slider.parent.parent;
var displayLabel = layerArea.findItemById("display_label");
displayLabel.setAttr("text", this.getAttr("text"));
this.dispatchEvent("bindingUpdated", "usr_cmd", "onCityItemUpdated");

logger.d("Demo: display label get focus, id=", this.id);

// 隐藏display label
var HIDE_ITEM_CLASS = "hide_item";
this.addClass(HIDE_ITEM_CLASS);

// 显示slider 和 indicator
var indicator = this.parent.findItemById("indicator_area");
logger.d("Demo: indicator id=", indicator.id);

var slider = this.parent.findItemById("slider_area");
logger.d("Demo: slider id=", slider.id);

// 让选中项重新获得焦点
var checkedItems = slider.findItemsByClass("selector_item_checked");
this.ownerPage.pushStates(indicator, "removeClass", HIDE_ITEM_CLASS,
    slider, "removeClass", HIDE_ITEM_CLASS,
    checkedItems[0], "requestFocus");
this.ownerPage.popStates(true);
logger.d("Demo: checkedItems[0]=", checkedItems[0].getAttr("text"));
logger.d("Demo: slider and indicator show.");
